$(document).ready(function () {

	// Wrap elements for syling purposes
	$(".listing h3").wrapInner('<span>');
	
	// Listings
	$(".listing-grid").addClass("listing-active");
	
	$(".listing-nav").click(function(e) {
		e.preventDefault();
	});	
	
	$(".listing-full").click(function() {
		$(".listing").addClass("listing-full");
		$(this).addClass("listing-active");
		$('.listing-grid').removeClass("listing-active");
	});
	
	$(".listing-grid").click(function() {
		$(".listing").removeClass("listing-full");
		$(this).addClass("listing-active");
		$('.listing-full').removeClass("listing-active");
	});
	
	// Activate lettering.js plugin for styling purposes
	$(".price").lettering();
	
	// Responsive Slides
	$(".rslides").responsiveSlides({
		auto: true,             // Boolean: Animate automatically, true or false
		speed: 800,             // Integer: Speed of the transition, in milliseconds
		timeout: 6000,          // Integer: Time between slide transitions, in milliseconds
		pager: false,           // Boolean: Show pager, true or false
		nav: true,              // Boolean: Show navigation, true or false
		playButton: true,
		random: false,          // Boolean: Randomize the order of the slides, true or false
		pause: false,            // Boolean: Pause on hover, true or false
		pauseControls: false,   // Boolean: Pause when hovering controls, true or false
		prevText: "Previous",   // String: Text for the "previous" button
		nextText: "Next",       // String: Text for the "next" button
		playText: "Play",       // String: Text for the "play" button, when paused
        pauseText: "Pause",     // String: Text for the "pause" button, when playing
		maxwidth: "",           // Integer: Max-width of the slideshow, in pixels
		controls: "",           // Selector: Where controls should be appended to, default is after the 'ul'
		namespace: "rslides",   // String: change the default namespace used
		before: function(){},   // Function: Before callback
		after: function(){}     // Function: After callback
	});
	
	$('#searchbox li ul li').click(function(){
		// add 'selected' class to item; remove it from others in dropdown
		$(this).siblings().removeClass('selected');
		$(this).addClass('selected');
	});

	$('#searchbox .mobile select').change(function(){
		// add 'selected' class to item; remove it from others in dropdown
		$(this).find(":selected").siblings().removeClass('selected');
		$(this).find(":selected").addClass('selected');
	});
	
	
	var spanval = $('#searchbox span').html();
	$('#search_market option').each(function(){
      if( this.text == spanval ){
        this.selected = true;
        return false;
      }
    });
 
	$('#searchsubmit').click(function() {
		// build search url based on options user chose
		u = 'http://www.invitationhomesforrent.com/apartmentsforrent/searchlisting.aspx?txtCity=';

		// get city
		v = $('#search_market').val();
		if(!v.length) {
			alert('Please include a city and state to search.');
		} else {
			u = u + encodeURIComponent(v);

			// get price -- format is "min-max" so we have to split it out
			d = '#search_price' + ( $('#searchbox ul').is(":visible")  ? '' : '2' );
			v = $(d).children('.selected').attr('value');
			if(v.length) {
				v = v.split('-');
				u = u+'&txtMinRent='+v[0]+'&txtMaxRent='+v[1];
		    }

			// get beds
			d = '#search_beds' + ( $('#searchbox ul').is(":visible")  ? '' : '2' );
			v = $(d).children('.selected').attr('value');
			u = u+'&cmbBeds='+v;

			// get baths
			d = '#search_baths' + ( $('#searchbox ul').is(":visible")  ? '' : '2' );
			v = $(d).children('.selected').attr('value');
			u = u+'&cmbBaths='+v;

			// redirect to results page
			window.location.href = u;
		}

	});

});



